mapper.select:
SELECT * FROM BOARD_IMG
WHERE BOARD_NO = ${boardNo}
ORDER BY IMG_ORDER

SELECT * FROM BOARD_IMG
WHERE BOARD_NO = ${boardNo}
ORDER BY IMG_ORDER

SELECT * FROM "AUTHOR"
WHERE BOARD_NO = ${boardNo}
ORDER BY AUTHOR_NO

SELECT * FROM "AUTHOR"
WHERE BOARD_NO = ${boardNo}
ORDER BY AUTHOR_NO

SELECT COUNT(*) FROM
"LIKE"
WHERE BOARD_NO = ${boardNo}
AND MEMBER_NO = ${memberNo}
